mapper.select:
SELECT orderM.id orderId, orderM.member_id userId, orderM.system_order_no orderSn, orderM.practical_pay totalAmount, orderM.order_status orderStatus, orderM.combo_level comboLevel, if(gom.id,gom.groupon_status,-1) isGroup, orderM.groupon_activity_id grouponActivityId FROM order_master orderM LEFT JOIN groupon_order_master gom on gom.order_id = orderM.id WHERE orderM.member_id = #{userId} and orderM.hidden = 0
and orderM.order_status = #{orderStatus}
ORDER BY orderM.create_time DESC
